import React, { useState, useEffect } from 'react';
import { TextField, Button, List, ListItem, Card, CardContent, Typography, Alert, Select, MenuItem, FormControl, InputLabel, Chip } from '@mui/material';

const NovelSearch = () => {
  const [keyword, setKeyword] = useState('');
  const [novels, setNovels] = useState([]);
  const [error, setError] = useState(null);
  const [genres, setGenres] = useState({});
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [order, setOrder] = useState('new');

  useEffect(() => {
    fetchGenres();
  }, []);

  const fetchGenres = async () => {
    try {
      const backendUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';
      const response = await fetch(`${backendUrl}/genres`);
      if (!response.ok) {
        throw new Error('Failed to fetch genres');
      }
      const data = await response.json();
      setGenres(data);
    } catch (error) {
      console.error('Error fetching genres:', error);
      setError('ジャンル一覧の取得に失敗しました');
    }
  };

  const handleSearch = async () => {
    setError(null);
    try {
      const backendUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';
      const genreParam = selectedGenres.join('-');
      const response = await fetch(`${backendUrl}/search?keyword=${encodeURIComponent(keyword)}&genre=${genreParam}&order=${order}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      if (data.error) {
        throw new Error(data.error);
      }
      setNovels(data);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError(error.message);
    }
  };

  const handleGenreChange = (event) => {
    setSelectedGenres(event.target.value);
  };

  const getLowercaseNcodeUrl = (ncode) => {
    return `https://ncode.syosetu.com/${ncode.toLowerCase()}/`;
  };

  return (
    <div style={{ padding: '20px' }}>
      <Typography variant="h4" component="h1" gutterBottom>
        なろう小説検索
      </Typography>
      <div style={{ display: 'flex', marginBottom: '20px', flexWrap: 'wrap', gap: '10px' }}>
        <TextField
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
          placeholder="キーワードを入力"
          variant="outlined"
          style={{ flexGrow: 1 }}
        />
        <FormControl variant="outlined" style={{ minWidth: 200 }}>
          <InputLabel>ジャンル</InputLabel>
          <Select
            multiple
            value={selectedGenres}
            onChange={handleGenreChange}
            label="ジャンル"
            renderValue={(selected) => (
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: 4 }}>
                {selected.map((value) => (
                  <Chip key={value} label={genres[value]} />
                ))}
              </div>
            )}
          >
            {Object.entries(genres).map(([id, name]) => (
              <MenuItem key={id} value={id}>{name}</MenuItem>
            ))}
          </Select>
        </FormControl>
        <FormControl variant="outlined" style={{ minWidth: 120 }}>
          <InputLabel>並び順</InputLabel>
          <Select
            value={order}
            onChange={(e) => setOrder(e.target.value)}
            label="並び順"
          >
            <MenuItem value="new">新着順</MenuItem>
            <MenuItem value="favnovelcnt">ブックマーク数順</MenuItem>
            <MenuItem value="reviewcnt">レビュー数順</MenuItem>
            <MenuItem value="hyoka">総合評価順</MenuItem>
          </Select>
        </FormControl>
        <Button variant="contained" color="primary" onClick={handleSearch}>
          検索
        </Button>
      </div>
      {error && (
        <Alert severity="error" style={{ marginBottom: '20px' }}>
          {error}
        </Alert>
      )}
      <List>
        {novels.map((novel) => (
          <ListItem key={novel.ncode}>
            <Card style={{ width: '100%', marginBottom: '10px' }}>
              <CardContent>
                <Typography variant="h6">{novel.title}</Typography>
                <Typography variant="body2">作者: {novel.writer}</Typography>
                <Typography variant="body2">ジャンル: {genres[novel.genre] || '不明'}</Typography>
                <Typography variant="body2">ブックマーク数: {novel.fav_novel_cnt}</Typography>
                <Typography variant="body2">評価点: {novel.all_point}</Typography>
                <Button
                  href={getLowercaseNcodeUrl(novel.ncode)}
                  target="_blank"
                  rel="noopener noreferrer"
                  color="primary"
                >
                  読む
                </Button>
              </CardContent>
            </Card>
          </ListItem>
        ))}
      </List>
    </div>
  );
};

export default NovelSearch;
