import React, { useState, useEffect } from 'react';
import { 
  TextField, Button, Grid, Card, CardContent, Typography, Alert, Select, MenuItem, 
  FormControl, InputLabel, Chip, Container, CircularProgress, Pagination, ThemeProvider, 
  createTheme, CssBaseline
} from '@mui/material';
import { styled } from '@mui/system';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      default: '#f5f5f5',
    },
  },
});

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
  '&:hover': {
    transform: 'translateY(-5px)',
    boxShadow: theme.shadows[4],
  },
}));

const NovelSearch = () => {
  const [keyword, setKeyword] = useState('');
  const [novels, setNovels] = useState([]); // 初期値を空の配列に設定
  const [error, setError] = useState(null);
  const [genres, setGenres] = useState({});
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [order, setOrder] = useState('new');
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    fetchGenres();
  }, []);

  const fetchGenres = async () => {
    try {
      const backendUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';
      const response = await fetch(`${backendUrl}/genres`);
      if (!response.ok) {
        throw new Error('Failed to fetch genres');
      }
      const data = await response.json();
      setGenres(data);
    } catch (error) {
      console.error('Error fetching genres:', error);
      setError('ジャンル一覧の取得に失敗しました');
    }
  };

  const handleSearch = async (pageNumber = 1) => {
    setError(null);
    setLoading(true);
    try {
      const backendUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';
      const genreParam = selectedGenres.join('-');
      const response = await fetch(`${backendUrl}/search?keyword=${encodeURIComponent(keyword)}&genre=${genreParam}&order=${order}&page=${pageNumber}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      if (data.error) {
        throw new Error(data.error);
      }
      setNovels(data.novels || []); // データがない場合は空の配列を設定
      setTotalPages(data.total_pages || 0);
      setPage(pageNumber);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError(error.message);
      setNovels([]); // エラー時は空の配列を設定
    } finally {
      setLoading(false);
    }
  };

  const handleGenreChange = (event) => {
    setSelectedGenres(event.target.value);
  };

  const getLowercaseNcodeUrl = (ncode) => {
    return `https://ncode.syosetu.com/${ncode.toLowerCase()}/`;
  };

  const handlePageChange = (event, value) => {
    handleSearch(value);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" style={{ padding: '20px' }}>
        <Typography variant="h4" component="h1" gutterBottom align="center">
          なろう小説検索
        </Typography>
        <Grid container spacing={2} alignItems="center" style={{ marginBottom: '20px' }}>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              fullWidth
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
              placeholder="キーワードを入力"
              variant="outlined"
            />
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <FormControl fullWidth variant="outlined">
              <InputLabel>ジャンル</InputLabel>
              <Select
                multiple
                value={selectedGenres}
                onChange={handleGenreChange}
                label="ジャンル"
                renderValue={(selected) => (
                  <div style={{ display: 'flex', flexWrap: 'wrap', gap: 4 }}>
                    {selected.map((value) => (
                      <Chip key={value} label={genres[value]} size="small" />
                    ))}
                  </div>
                )}
              >
                {Object.entries(genres).map(([id, name]) => (
                  <MenuItem key={id} value={id}>{name}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <FormControl fullWidth variant="outlined">
              <InputLabel>並び順</InputLabel>
              <Select
                value={order}
                onChange={(e) => setOrder(e.target.value)}
                label="並び順"
              >
                <MenuItem value="new">新着順</MenuItem>
                <MenuItem value="favnovelcnt">ブックマーク数順</MenuItem>
                <MenuItem value="reviewcnt">レビュー数順</MenuItem>
                <MenuItem value="hyoka">総合評価順</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} sm={6} md={2}>
            <Button fullWidth variant="contained" color="primary" onClick={() => handleSearch(1)} disabled={loading}>
              検索
            </Button>
          </Grid>
        </Grid>
        {error && (
          <Alert severity="error" style={{ marginBottom: '20px' }}>
            {error}
          </Alert>
        )}
        {loading ? (
          <div style={{ display: 'flex', justifyContent: 'center', padding: '20px' }}>
            <CircularProgress />
          </div>
        ) : (
          <Grid container spacing={3}>
            {Array.isArray(novels) && novels.length > 0 ? (
              novels.map((novel) => (
                <Grid item xs={12} sm={6} md={4} key={novel.ncode}>
                  <StyledCard>
                    <CardContent>
                      <Typography variant="h6" gutterBottom>{novel.title}</Typography>
                      <Typography variant="body2">作者: {novel.writer}</Typography>
                      <Typography variant="body2">ジャンル: {genres[novel.genre] || '不明'}</Typography>
                      <Typography variant="body2">ブックマーク数: {novel.fav_novel_cnt.toLocaleString()}</Typography>
                      <Typography variant="body2">評価点: {novel.all_point}</Typography>
                    </CardContent>
                    <CardContent style={{ marginTop: 'auto' }}>
                      <Button
                        fullWidth
                        variant="outlined"
                        color="primary"
                        href={getLowercaseNcodeUrl(novel.ncode)}
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        読む
                      </Button>
                    </CardContent>
                  </StyledCard>
                </Grid>
              ))
            ) : (
              <Grid item xs={12}>
                <Typography variant="body1" align="center">
                  小説が見つかりませんでした。別のキーワードで検索してみてください。
                </Typography>
              </Grid>
            )}
          </Grid>
        )}
        {totalPages > 1 && (
          <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
            <Pagination
              count={totalPages}
              page={page}
              onChange={handlePageChange}
              color="primary"
            />
          </div>
        )}
      </Container>
    </ThemeProvider>
  );
};

export default NovelSearch;
