import React, { useState } from 'react';
import { TextField, Button, List, ListItem, Card, CardContent, Typography, Alert } from '@mui/material';

const NovelSearch = () => {
  const [keyword, setKeyword] = useState('');
  const [novels, setNovels] = useState([]);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    setError(null);
    try {
      // バックエンドのURLを環境変数から取得するか、直接指定します
      const backendUrl = process.env.REACT_APP_API_URL || 'http://localhost:8000';
      const response = await fetch(`${backendUrl}/search?keyword=${encodeURIComponent(keyword)}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      if (data.error) {
        throw new Error(data.error);
      }
      setNovels(data);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError(error.message);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <Typography variant="h4" component="h1" gutterBottom>
        なろう小説検索
      </Typography>
      <div style={{ display: 'flex', marginBottom: '20px' }}>
        <TextField
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
          placeholder="キーワードを入力"
          variant="outlined"
          style={{ marginRight: '10px' }}
        />
        <Button variant="contained" color="primary" onClick={handleSearch}>
          検索
        </Button>
      </div>
      {error && (
        <Alert severity="error" style={{ marginBottom: '20px' }}>
          {error}
        </Alert>
      )}
      <List>
        {novels.map((novel) => (
          <ListItem key={novel.ncode}>
            <Card style={{ width: '100%', marginBottom: '10px' }}>
              <CardContent>
                <Typography variant="h6">{novel.title}</Typography>
                <Typography variant="body2">{novel.writer}</Typography>
                <Button
                  href={`https://ncode.syosetu.com/${novel.ncode}/`}
                  target="_blank"
                  rel="noopener noreferrer"
                  color="primary"
                >
                  読む
                </Button>
              </CardContent>
            </Card>
          </ListItem>
        ))}
      </List>
    </div>
  );
};

export default NovelSearch;
